$(document).ready(function(){$(".table-list-row").each(function(index,value){var row=$(value);var child=row.find('.table-list-title > a');if(child.length===1){if(child.attr('href')){row.unbind('click')
row.bind('click',function(e){e.preventDefault();e.stopPropagation();location.href=child.attr('href')})
row.css('cursor','pointer')}}})});(function(){KB.onClick('#modal-overlay',function(){KB.modal.close()});$(document).on('click','.dropdown-menu',function(e){if(document.getElementById('dropdown')!==null){e.preventDefault();e.stopImmediatePropagation();KB.trigger('dropdown.beforeDestroy');$("#dropdown").remove()}})}())